import React from "react";
import { useState } from "react";
import Axios from 'axios'
import ReactDOM from 'react-dom'

import { FaRegEdit } from 'react-icons/fa';

const Admin = () => {

    const [title, setTitle] = useState("");
    const [price, setPrice] = useState(0);
    const [location, setLocation] = useState("");
    const [image, setImage] = useState("")

    const [workshopList, setWorkshopList] = useState([])

    const [newTitle, setNewTitle] = useState("")

    Axios.get('http://localhost:3001/home').then((response) => {
      setWorkshopList(response.data);
  })

    const addWorkshop = () => {
        Axios.post('http://localhost:3001/admin', {
            title: title,
            location: location, 
            price: price,
            image: image,
        }).then(() => {
            setWorkshopList([...workshopList, {
              title: title,
              location: location, 
              price: price,
              image: image,
            }])
            console.log('success')
        })
    }

  const updateTitle = (id) => {
    Axios.put('http://localhost:3001/update', {title: newTitle, id: id}).then(
    (response) => {
      setWorkshopList(
        workshopList.map((val) => {
          return val.id == id ? {
              id: val.id,
              title: newTitle,
              location: val.location,
              price: val.price
            }
            : val
        })
      )
    })
}

  const editFunction = () => {

  }

    return (
        <section className="main">
            <h1>THIS IS THE ADMIN PAGE</h1>
            <div className="inputs">
                <label>Workshop Title</label>
                <input type="text" onChange={(event) => {
                    setTitle(event.target.value)}}/>
                <label>Location</label>
                <input type="text" onChange={(event) => {
                    setLocation(event.target.value)}}/>
                <label>Price</label>
                <input type="number"
                  onChange={(event) => {
                  setPrice(event.target.value)
                  }}/>
                <p>Select Image</p>
                <label>Angmardana</label>
                <input type="radio" value="angamardana" onChange={(event) => {
                    setImage(event.target.value)}}/>
                <button onClick={addWorkshop}>Add Workshop</button>
            </div>

            <div>
            <img src="img/sk.jpg" alt=""></img>
            </div>

            {/* <input type="radio" id="html" name="fav_language" value="HTML">
Â  <label for="html">HTML</label><br> */}

            {/* <div className="results">
            <h2>Show Workshops</h2>
              <button onClick={getWorkshops}>Show Workshops</button>
            </div> */}

            <div className="workshops">
              {workshopList.map((val, key) => {
                return (
                  <div className="card">
                    <img src="img/`${val.image}`" alt=""></img>
                    <h2>{val.title} <i class="far fa-edit"></i></h2>
                    <h4>{val.location}</h4>
                    <p>{val.price}</p>

                    <div>
                     <FaRegEdit onClick={() => {}}/>
                    </div>

                    <input type="text" placeholder="edit this" onChange={(event) => {
                  setNewTitle(event.target.value)
                  }} />
                    <button onClick={() => {updateTitle(val.id)}}>Update</button>
                  </div>
                )
              })}
            </div>
        </section>
)};

export default Admin;
