import './App.css';
// import Admin from "./components/Admin.js"
import { useState } from "react";
import Axios from 'axios'

//admin page figure out
import { BrowserRouter as Router, Switch, Route } from 'react-router-dom';
import Admin from './components/Admin';


function App() {

    const [title, setTitle] = useState("");
    const [price, setPrice] = useState(0);
    const [location, setLocation] = useState("");

    const [workshopList, setWorkshopList] = useState([])

    const addWorkshop = () => {
        Axios.post('http://localhost:3001/admin', {
            title: title,
            location: location, 
            price: price,
        }).then(() => {
            setWorkshopList([...workshopList, {
              title: title,
              location: location, 
              price: price,
            }])
            console.log('success')
        })
    }

    const getWorkshops = () => {
      Axios.get('http://localhost:3001/home').then((response) => {
          setWorkshopList(response.data);
      })
  }

return (
  <Router>
  <Switch>  
    <Route path="/admin" exact component={Admin} />
    <section className="main">
        <h1>Add a workshop</h1>
        <div className="inputs">
            <label>Workshop Title</label>
            <input type="text" onChange={(event) => {
                setTitle(event.target.value)}}/>
            <label>Location</label>
            <input type="text" onChange={(event) => {
                setLocation(event.target.value)}}/>
            <label>Price</label>
            <input type="number"
              onChange={(event) => {
              setPrice(event.target.value)
              }} 
              />
            <button onClick={addWorkshop}>Add Workshop</button>
        </div>

        <div className="results">
        <h2>Show Workshops</h2>
          <button onClick={getWorkshops}>Show Workshops</button>
        </div>

        <div className="workshops">
          {workshopList.map((val, key) => {
            return (
              <div className="card">
                <img src="{val." alt=""></img>
                <h2>{val.title}</h2>
                <h4>{val.location}</h4>
                <p>{val.price}</p>
                <button>more info</button> <button>Register</button>
              </div>
            )
          })}

        </div>
    </section>
  </Switch>  
</Router>
)};


export default App;
