import './App.css';
// import Admin from "./components/Admin.js"
import React from "react";
import { useState } from "react";
import Axios from 'axios'

function App() {

    const [title, setTitle] = useState("");
    const [price, setPrice] = useState(0);
    const [location, setLocation] = useState("");

    const [workshopList, setWorkshopList] = useState([])

    const addWorkshop = () => {
        Axios.post('http://localhost:3001/admin', {
            title: title,
            location: location, 
            price: price,
        }).then(() => {
            console.log('success')
        })
    }

    const getWorkshops = () => {
      Axios.get('http://localhost:3001/home').then((response) => {
          setWorkshopList(response.data);
      })
  }

return (
    <section className="main">
        <h1>Add a workshop</h1>
        <div className="inputs">
            <label>Workshop Title</label>
            <input type="text" onChange={(event) => {
                setTitle(event.target.value)}}/>
            <label>Location</label>
            <input type="text" onChange={(event) => {
                setLocation(event.target.value)}}/>
            <label>Price</label>
            <input type="number"
              onChange={(event) => {
              setPrice(event.target.value)
              }} 
              />
            <button onClick={addWorkshop}>Add Workshop</button>
        </div>

        <div className="workshops">
          <h2>Show Workshops</h2>
          <button onClick={getWorkshops}>Show Workshops</button>

          {workshopList.map((val, key) => {
            return (
              <div className="card">
                <img src="" alt=""></img>
                <h2>Yoga Class</h2>
                <h4>2 Session Workshop 23rd & 24th Oct</h4>
                <p>Lorem ipsum dolor sit, amet consectetur adipisicing elit. Illo quae impedit quibusdam odit fugit ab quas magni repellat, earum laborum?</p>
                <button>more info</button>
              </div>
            )
          })}

        </div>
    </section>
)};


export default App;
