import React from "react";
import '../App.css'
import { useEffect, useState } from 'react';
import Axios from 'axios'
import Navbar from './Navbar';


function Yogaclasses() {

    const [workshopList, setWorkshopList] = useState([])

    useEffect(() => {
        Axios.get('http://localhost:3001/home').then((response) => {
          setWorkshopList(response.data);
      })
    }, [])

    const addImage = (name) => {
        const pic = name
        return `img/${pic}.jpg`
    }

    return (
        <section className="yogaclasses">

        <Navbar />

        <div className="results">
            <h2>Hatha Yoga Workshop Schedule</h2>
            <div className="controls">
                    <button class="control" data-location="all">All</button>
                    <button class="control" data-location="zurich">Zurich</button>
                    <button class="control" data-location="kloten">Kloten</button>
                    <button class="control" data-location="glasgow">Glasgow</button>>
            </div>
        </div>

        <div className="workshops">
          {workshopList.map((val, key) => {
            return (
              <div className="card">
                <div className="imagebox">
                <img src={addImage(val.image)} alt=""></img>                

                </div>
                {/* <img src={addImage(val.image)} alt=""></img>                 */}
                <h2 className="inputEdit">{val.title}</h2>
                <h4 className="inputEdit">{val.location}</h4>
                <p className="inputEdit">{val.date}</p>
                <p className="inputEdit">{val.price}</p>
                <button>Register</button>
              </div>
            )
          })}

        </div>
    </section>
    )
}

export default Yogaclasses
