import React, { useEffect, useState } from 'react';
import anga from '../src-imgs/imageSlider/ag.jpg';
import { IoIosArrowBack } from "react-icons/io";
import { IoIosArrowForward } from "react-icons/io";


  const Slider = () => {

    const sliderItems = [
      {
      title: 'Angamardana',
      desc: 'Angamardana is a system rooted in yoga that offers to everyone the opportunity to invigorate the body and to reach peak physical and mental health.',
      image: `img/angamardana.jpg`
    },
    {
      title: 'Yogasanas',
      desc: 'The word asana literally means a posture. Out of the innumerable asanas a body can assume, 84 have been identified as Yogasanas, through which one can transform the body and mind into a possibility for ultimate well-being. By consciously forming the body into a certain posture, one creates a conducive passage for energy flow that can elevate one’s consciousness.',
      image: `img/yogasanas.jpg`
    }
  ]
  return (
    <section className="mainSection">
      <div className="content1">

      <div className="content2">
        <h1>Angamardana</h1>

        <p>Angamardana is a system rooted in yoga that offers to everyone the opportunity to invigorate the body and to reach peak physical and mental health.</p>

        <p>“Angamardana” means gaining complete mastery over the limbs, organs and other parts of the body. This practice revitalizes the body on all levels including the muscles, blood circulation, skeletal structure, nervous system, and the basic energy system. It strengthens the spine, builds physical strength, fitness and tenacity, taking years off the body.​</p>
      </div>
      <div className='content3'>
        <IoIosArrowBack className="forwardBack"/>
        <img className="sliderImage" src={anga} alt="" />
        <IoIosArrowForward className="forwardArrow" />
      </div>

      </div>
    </section>
  )};

export default Slider;
