import React, { useEffect, useState, Fragment } from 'react';
import Axios from 'axios'

import Front from './Front';
import Back from './Back';
import Error from '../Error';



const showWorkshops = () => {

    const [title, setTitle] = useState("");
    const [price, setPrice] = useState(0);
    const [location, setLocation] = useState("");
    const [image, setImage] = useState("")
    const [date, setDate] = useState("")

    const [workshopList, setWorkshopList] = useState([])

    const [editId, setEditId] = useState(3);

    const [showError, setShowError] = useState(false)

    const [showFront, setShowFront] = useState(true);

    useEffect(() => {
        Axios.get('http://localhost:3001/home').then((response) => {
        setWorkshopList(response.data)
        }).catch(err => {
            setShowError(true)
            console.log(err)
          });
      }, [])  


    const handleEditClick = () => {

    }

    const editButtonFunction = () => {
        setShowFront(false)
    }


    const saveButton = () => {
        setShowFront(true)
    }

    const resetError = () => {
        setShowError(false)
        }


    return (
        <section >


            <p>Select Image</p>

            <div className="images">

                <label>Angmardana</label>
                <input type="radio" value="angamardana" onChange={(event) => {
                    setImage(event.target.value)}}/>
                <label>Yogasanas</label>
                <input type="radio" value="yogasanas" onChange={(event) => {
                    setImage(event.target.value)}}/>
            </div>

            <div>
                <button onClick={addWorkshop}>Add Workshop</button>
            </div>


            {showError
                ? <Error />
                : resetError
            }

            {showFront 
                ? <Front workshopList={workshopList} editButton={editButtonFunction}/>
                : <Back workshopList={workshopList} setWorkshopList={setWorkshopList} saveButton={saveButton}/>
            }

            {/* {workshopList.map((workshop) => (
                workshop.id === editId ? console.log('front') :
                console.log('back')
            ))} */}



            {/* {workshopList.map((workshop) => {
                if (editId === workshop.id){
                    <Front workshopList={workshopList} flipButton={flipCard}/>
                } else (
                    <Back workshopList={workshopList} setWorkshopList={setWorkshopList} saveButton={saveButton}/>
                )
            })} */}



        </section>
)};

export default showWorkshops;

