import React, { useEffect, useState, Fragment } from 'react';
import Axios from 'axios'

import Front from './admin-page/Front';
import Back from './admin-page/Back';
import Error from './Error';
import { FaCaretDown } from 'react-icons/fa';



const ShowWorkshops = () => {

    const [workshopList, setWorkshopList] = useState([])

    const [workshopId, setWorkshopId] = useState(null)

    const [showFront, setShowFront] = useState(true);

    const [showError, setShowError] = useState(false)

    useEffect(() => {
        Axios.get('http://localhost:3001/home').then((response) => {
        setWorkshopList(response.data)
        }).catch(err => {
            setShowError(true)
            console.log(err)
          });
        }, []) 

    const editButtonFunction = () => {
        // setWorkshopId(id)
        setShowFront(false)
        console.log()
    } 

    const saveButton = () => {
        setShowFront(true)
    }    

    const resetError = () => {
        setShowError(false)
    }

    return (
        <section >
            { showError
                ? <Error />
                : resetError
            }


            <Front workshopList={workshopList} editButton={editButtonFunction}/>
            <Back workshopList={workshopList} setWorkshopList={setWorkshopList} saveButton={saveButton} workshopId={workshopId}/>



        {/* { showFront 
            ? <Front workshopList={workshopList} editButton={editButtonFunction}/>
            : <Back workshopList={workshopList} setWorkshopList={setWorkshopList} saveButton={saveButton}/>   
        } */}
        </section>
)};

