import React, { useEffect, useState } from 'react';
import './Slider.css'

// import anga from '../src-imgs/imageSlider/ag.jpg';
import { IoIosArrowBack } from "react-icons/io";
import { IoIosArrowForward } from "react-icons/io";

import SliderData from './SliderData';
import SliderBtn from './SliderBtn';



const Slider = () => {

  const [slideIndex, setSlideIndex] = useState(1)

  const nextSlide = () => {
    if( slideIndex !== SliderData.length){
      setSlideIndex(slideIndex + 1)
    } else if (slideIndex === SliderData.length) {
      setSlideIndex(1)
    }
  }

  const prevSlide = () => {
    if( slideIndex !== 1){
      setSlideIndex(slideIndex - 1)
    } else if (slideIndex === 1) {
      setSlideIndex(SliderData.length)
    }
  }

  return (
    <section className="container-slider">
      <div className='sliderBox'>


      <div className="textSide">
        <h1>What we teach</h1>      
        <p>The Isha Yoga practices offer an introduction to meditation and Workshops can be tailored for individual needs and medical requirements.
        </p>

        <p>The practices are taught in a workshop format where you will be learning a complete Hatha Yoga practice over multiple sessions and when you finish the program you will be competent to do the Yoga practice by yourself in the comfort of your home.
        </p>

      </div>

      <div className='button'><SliderBtn moveSlide={prevSlide} direction={"prev"}/> </div>

      <div className='slider'>
        {SliderData.map((obj, index) => {
            return (
              <div 
                key={obj.id}
                className={slideIndex === index + 1 ? "slide active-anim" : "slide"}>
                  <img src={process.env.PUBLIC_URL + `/img/slider/img${index + 1}.jpg`} />

               {/* <div className='textSide'><h1>{obj.title}</h1></div> */}

               </div>
                )
              })}

        {/* <SliderBtn moveSlide={nextSlide} direction={"next"}/> 
        <SliderBtn moveSlide={prevSlide} direction={"prev"}/>  */}

      </div>
      <div className='button'><SliderBtn moveSlide={nextSlide} direction={"next"}/> </div>


    </div>

    </section>
  )};

export default Slider;

